import React from 'react';
import './AdvantageSection.css';

const AdvantageSection = () => (
  <div className="advantage-section">
    <h2>5. Advantage over Competition</h2>
    <div className="advantage-item">
      <h3>Safety Focus</h3>
      <p>We prioritize user safety by vetting recommendations and providing insights on the safety of locations.</p>
    </div>
    <div className="advantage-item">
      <h3>Personalization</h3>
      <p>TravelSmart provides custom recommendations based on individual user preferences and local insights.</p>
    </div>
    <div className="advantage-item">
      <h3>Dynamic Ratings</h3>
      <p>Our platform updates ratings in real-time based on local popularity and seasonal trends, ensuring that users receive the most relevant suggestions.</p>
    </div>
  </div>
);

export default AdvantageSection;
